@import url("https://fonts.googleapis.com/css?family=MedievalSharp|IM+Fell+English");
body {
  font-family: sans-serif;
  margin: 0;
  padding: 0; }

body,
* {
  box-sizing: border-box;
  font-family: "IM Fell English", serif; }

ul {
  list-style: none;
  padding: 0px 0px; }

header {
  padding: 1rem 2rem;
  background-image: linear-gradient(190deg, #30cfd0 0%, #330867 100%);
  text-align: center; }
  header h1 {
    margin: 0;
    font-family: "MedievalSharp", cursive;
    color: #106d86;
    color: #fff; }

footer {
  padding: 1rem 2rem;
  background-image: linear-gradient(190deg, #325a92 0%, #330867 100%); }
  footer span {
    color: #987ae1; }

.prime-container {
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  background-color: #f2f2f2;
  min-height: 100vh; }

.content {
  display: flex;
  flex: 1;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  padding: 2rem 1rem; }

.swapiWrapper {
  background-color: #fff;
  width: 100%;
  max-width: 600px;
  margin-bottom: 50px;
  padding: 10px 15px;
  box-shadow: 0 0 2px #ccc;
  border-radius: 5px; }

.todoWrapper {
  flex: 0;
  width: 100%;
  max-width: 600px;
  padding: 10px 15px;
  border-radius: 5px;
  background-image: linear-gradient(15deg, #32508d 0%, #31a5ba 100%); }
  .todoWrapper h2 {
    font-size: 22px;
    text-align: center;
    color: #fff;
    margin: 0 0 10px; }

.list-item {
  display: flex;
  align-items: center;
  background: white;
  border-radius: 4px;
  margin-bottom: 0.5rem;
  padding: 0.3rem 0.5rem;
  color: #333; }
  .list-item input {
    display: none;
    flex: 1;
    margin-right: 1rem;
    font-size: 18px;
    padding: 3px 3px 3px 10px; }
  .list-item span {
    flex: 1;
    margin-right: 1rem;
    font-size: 18px;
    padding: 6px 3px 6px 10px;
    line-height: 1;
    vertical-align: middle; }
  .list-item button {
    display: flex;
    align-items: center;
    justify-content: center;
    margin-left: auto;
    margin-right: 5px;
    font-size: 14px;
    border: none;
    color: white;
    border-radius: 50%;
    width: 25px;
    height: 25px;
    cursor: pointer;
    font-weight: bold; }
  .list-item.edit input {
    display: block; }
  .list-item.edit span {
    /*hides the span from view*/
    display: none; }
  .list-item.item-completed span {
    text-decoration: line-through; }

.delete-button {
  background-color: #f36858; }

.toggle-button {
  background-color: #3afbba; }
